  <header class="app-header" [class.header-scrolled]="showScrollTop">
    <div class="container">
      <div class="header-content">
        <div class="header-left">
          <button
            *ngIf="showBackButton"
            class="back-button"
            (click)="goBack()"
            aria-label="–í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞–∑–∞–¥"
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" aria-hidden="true">
              <path d="M19 12H5M12 19l-7-7 7-7" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>

          <h1 class="app-title" (click)="goHome()" role="button" tabindex="0" aria-label="–ì–ª–∞–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞">
            {{ title }}
          </h1>
        </div>

        <nav class="header-nav" aria-label="–û—Å–Ω–æ–≤–Ω–∞—è –Ω–∞–≤–∏–≥–∞—Ü–∏—è">
          <a
            routerLink="/"
            routerLinkActive="active"
            [routerLinkActiveOptions]="{exact: true}"
