import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { trigger, state, style, transition, animate } from '@angular/animations';
import { Movie } from '../../models/movie.model';

@Component({
  selector: 'app-movie-card',
  templateUrl: './movie-card.component.html',
  styleUrls: ['./movie-card.component.scss'],
  animations: [
    trigger('cardHover', [
      state('normal', style({
        transform: 'scale(1)',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.15)'
      })),
      state('hovered', style({
        transform: 'scale(1.03)',
        boxShadow: '0 8px 16px rgba(0, 0, 0, 0.25)'
      })),
      transition('normal => hovered', [
        animate('0.3s ease')
      ]),
      transition('hovered => normal', [
        animate('0.2s ease')
      ])
    ]),
    trigger('fadeIn', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(20px)' }),
        animate('0.5s ease-out', style({ opacity: 1, transform: 'translateY(0)' }))
      ])
    ])
  ]
})
export class MovieCardComponent implements OnInit {
  @Input() movie!: Movie;
  @Input() showDetails: boolean = false;
  @Output() cardClick = new EventEmitter<number>();

  hoverState: 'normal' | 'hovered' = 'normal';
  ratingColor: string = '';

  ngOnInit(): void {
    this.setRatingColor();
  }

  onCardClick(): void {
    this.cardClick.emit(this.movie.id);
  }

  onMouseEnter(): void {
    this.hoverState = 'hovered';
  }

  onMouseLeave(): void {
    this.hoverState = 'normal';
  }

  private setRatingColor(): void {
    if (this.movie.rating >= 8.5) {
      this.ratingColor = 'success';
    } else if (this.movie.rating >= 7.0) {
      this.ratingColor = 'warning';
    } else {
      this.ratingColor = 'error';
    }
  }

  getRatingClass(): string {
    return `rating-${this.ratingColor}`;
  }
}
<div
  class="movie-card"
  [@cardHover]="hoverState"
  [@fadeIn]
  (click)="onCardClick()"
  (mouseenter)="onMouseEnter()"
  (mouseleave)="onMouseLeave()"
  role="button"
  tabindex="0"
>
  <div class="movie-card__poster">
    <img
      [src]="movie.poster"
      [alt]="movie.title"
      loading="lazy"
    />
    <div class="movie-card__rating" [ngClass]="getRatingClass()">
      {{ movie.ratingFormatted }}
    </div>
  </div>

  <div class="movie-card__content">
    <h3 class="movie-card__title">{{ movie.title }}</h3>
    <div class="movie-card__meta">
      <span class="movie-card__year">{{ movie.year }}</span>
      <span class="movie-card__duration">{{ movie.durationFormatted }}</span>
    </div>

    <div class="movie-card__genres">
      {{ movie.genresFormatted }}
    </div>

    <div *ngIf="showDetails" class="movie-card__details">
      <p class="movie-card__description">{{ movie.description }}</p>
      <div class="movie-card__director">
        <strong>Режиссер:</strong> {{ movie.director }}
      </div>
      <div class="movie-card__cast" *ngIf="movie.cast.length > 0">
        <strong>В ролях:</strong> {{ movie.castFormatted }}
        <span *ngIf="movie.cast.length > 3">...</span>
      </div>
    </div>
  </div>
</div>
