export class Movie {
  id;
  title;
  originalTitle;
  year;
  description;
  rating;
  duration;
  genres;
  director;
  cast;
  poster;
  background;
  constructor(id, title, originalTitle, year, description, rating, duration, genres, director, cast, poster, background) {
    this.id = id;
    this.title = title;
    this.originalTitle = originalTitle;
    this.year = year;
    this.description = description;
    this.rating = rating;
    this.duration = duration;
    this.genres = genres;
    this.director = director;
    this.cast = cast;
    this.poster = poster;
    this.background = background;
  }
  get durationFormatted() {
    const hours = Math.floor(this.duration / 60);
    const minutes = this.duration % 60;
    return `${hours}ч ${minutes}м`;
  }
  get ratingFormatted() {
    return this.rating.toFixed(1);
  }
  get genresFormatted() {
    return this.genres.join(', ');
  }
  get castFormatted() {
    return this.cast.slice(0, 3).join(', ');
  }
  static fromJson(json) {
    return new Movie(json.id, json.title, json.originalTitle, json.year, json.description, json.rating, json.duration, json.genres, json.director, json.cast, json.poster, json.background);
  }
}
