import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Location } from '@angular/common';
import { Subject } from 'rxjs';
import { takeUntil, switchMap, finalize } from 'rxjs/operators';
import { Movie } from '../../models/movie.model';
import { MovieService } from '../../services/movie.service';

@Component({
  selector: 'app-movie-detail',
  templateUrl: './movie-detail.component.html',
  styleUrls: ['./movie-detail.component.scss']
})
export class MovieDetailComponent implements OnInit, OnDestroy {
  movie: Movie | null = null;
  isLoading = true;
  error: string | null = null;
  recommendedMovies: Movie[] = [];
  defaultImage = 'assets/images/placeholder.jpg';

  private destroy$ = new Subject<void>();

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private location: Location,
    private movieService: MovieService
  ) {}

  ngOnInit(): void {
    this.loadMovie();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  loadMovie(): void {
    this.route.params.pipe(
      switchMap(params => {
        const id = +params['id'];
        this.isLoading = true;
        this.error = null;

        return this.movieService.getMovieById(id).pipe(
          finalize(() => {
            this.isLoading = false;
          })
        );
      }),
      takeUntil(this.destroy$)
    ).subscribe({
      next: (movie) => {
        this.movie = movie;
        this.loadRecommendedMovies();
      },
      error: (error) => {
        this.error = error.message || 'Не удалось загрузить информацию о фильме';
        console.error('Error loading movie:', error);
      }
    });
  }

  loadRecommendedMovies(): void {
    this.movieService.getRecommendedMovies(4)
      .pipe(takeUntil(this.destroy$))
      .subscribe({
        next: (movies) => {
          this.recommendedMovies = movies.filter(m => m.id !== this.movie?.id).slice(0, 3);
        },
        error: (error) => {
          console.error('Error loading recommended movies:', error);
        }
      });
  }

  goBack(): void {
    this.location.back();
  }

  onRecommendedMovieClick(movieId: number): void {
    this.router.navigate(['/movie', movieId]);
  }

  getRatingStars(): number[] {
    if (!this.movie) return [];
    const fullStars = Math.floor(this.movie.rating / 2);
    return Array(5).fill(0).map((_, i) => i < fullStars ? 1 : 0.5);
  }

  getSafeImageUrl(url: string): string {
    if (!url || url.trim() === '') {
      return this.defaultImage;
    }

    if (url.startsWith('assets/')) {
      return url;
    }

    if (url.startsWith('/')) {
      return url;
    }

    if (url.startsWith('http://') || url.startsWith('https://')) {
      return url;
    }

    return `assets/images/${url}`;
  }

  handleImageError(event: any): void {
    console.warn(`Ошибка загрузки изображения: ${event.target.src}`);
    event.target.src = this.defaultImage;
    event.target.alt = 'Изображение временно недоступно';
    event.target.title = 'Изображение временно недоступно';
  }

  getMovieColor(): string {
    if (!this.movie) return '#1a237e';

    const colors = [
      '#1a237e', // Начало
      '#311b92', // Темный рыцарь
      '#004d40', // Интерстеллар
      '#3e2723', // Помни
      '#1b5e20', // Матрица
      '#bf360c'  // Криминальное чтиво
    ];
    return colors[this.movie.id % colors.length];
  }

  showColorFallback(): boolean {
    return !this.movie?.poster || this.movie.poster.trim() === '';
  }
}
<div class="movie-detail">
  <!-- Загрузка -->
  <div *ngIf="isLoading" class="movie-detail__loading">
    <div class="loading-spinner"></div>
    <p>Загружаем информацию о фильме...</p>
  </div>

  <!-- Ошибка -->
  <div *ngIf="error && !isLoading" class="movie-detail__error">
    <div class="error-message">
      <h2>Ошибка</h2>
      <p>{{ error }}</p>
      <div class="error-actions">
        <button
          class="back-button"
          (click)="goBack()"
          aria-label="Вернуться назад"
        >
          Назад
        </button>
        <button
          class="retry-button"
          (click)="loadMovie()"
          aria-label="Попробовать снова"
        >
          Попробовать снова
        </button>
      </div>
    </div>
  </div>

  <!-- Детали фильма -->
  <div *ngIf="movie && !isLoading && !error" class="movie-detail__content">
    <!-- Заголовок и кнопка назад -->
    <header class="movie-detail__header">
      <button
        class="back-button"
        (click)="goBack()"
        aria-label="Вернуться назад"
      >
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" aria-hidden="true">
          <path d="M19 12H5M12 19l-7-7 7-7" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
        </svg>
        Назад
      </button>
      <h1 class="movie-detail__title">{{ movie.title }}</h1>
      <p class="movie-detail__original-title">{{ movie.originalTitle }}</p>
    </header>

    <!-- Основной контент -->
    <main class="movie-detail__main">
      <!-- Постер и рейтинг -->
      <div class="movie-detail__poster-section">
        <div class="poster-container">
          <!-- Изображение или цветной блок -->
          <ng-container *ngIf="!showColorFallback()">
            <img
              [src]="getSafeImageUrl(movie.poster)"
              [alt]="movie.title"
              class="movie-poster"
              loading="lazy"
              (error)="handleImageError($event)"
            />
          </ng-container>

          <div
            *ngIf="showColorFallback()"
            class="movie-poster-fallback"
            [style.background]="'linear-gradient(135deg, ' + getMovieColor() + ' 0%, #333 100%)'"
          >
            <div class="fallback-text">{{ movie.title }}</div>
          </div>

          <div class="rating-badge">
            <span class="rating-value">{{ movie.ratingFormatted }}</span>
            <div class="rating-stars">
              <span *ngFor="let star of getRatingStars(); let i = index" class="star">
                <svg *ngIf="star === 1" width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
                </svg>
                <svg *ngIf="star === 0.5" width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77V2z"/>
                </svg>
                <svg *ngIf="star === 0" width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"
                        fill-opacity="0.3"/>
                </svg>
              </span>
            </div>
          </div>
        </div>
      </div>

      <!-- Информация о фильме -->
      <div class="movie-detail__info-section">
        <div class="info-grid">
          <div class="info-item">
            <h3 class="info-label">Год выпуска</h3>
            <p class="info-value">{{ movie.year }}</p>
          </div>

          <div class="info-item">
            <h3 class="info-label">Продолжительность</h3>
            <p class="info-value">{{ movie.durationFormatted }}</p>
          </div>

          <div class="info-item">
            <h3 class="info-label">Режиссер</h3>
            <p class="info-value">{{ movie.director }}</p>
          </div>

          <div class="info-item">
            <h3 class="info-label">Жанры</h3>
            <div class="genres-list">
              <span *ngFor="let genre of movie.genres" class="genre-tag">
                {{ genre }}
              </span>
            </div>
          </div>

          <div class="info-item full-width">
            <h3 class="info-label">В главных ролях</h3>
            <p class="info-value">{{ movie.castFormatted }}</p>
          </div>

          <div class="info-item full-width">
            <h3 class="info-label">Описание</h3>
            <p class="description">{{ movie.description }}</p>
          </div>
        </div>
      </div>
    </main>

    <!-- Рекомендованные фильмы -->
    <footer *ngIf="recommendedMovies.length > 0" class="movie-detail__recommendations">
      <h2 class="recommendations-title">Рекомендуем посмотреть</h2>
      <div class="recommendations-grid">
        <app-movie-card
          *ngFor="let recommendedMovie of recommendedMovies"
          [movie]="recommendedMovie"
          [showDetails]="false"
          (cardClick)="onRecommendedMovieClick(recommendedMovie.id)"
          class="recommendation-card"
        ></app-movie-card>
      </div>
    </footer>
  </div>
</div>
