import { Component, OnInit } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'Каталог фильмов';
  showBackButton = false;
  currentRoute = '';

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.setupRouteTracking();
  }

  private setupRouteTracking(): void {
    this.router.events.pipe(
      filter(event => event instanceof NavigationEnd)
    ).subscribe((event: any) => {
      this.currentRoute = event.url;
      this.showBackButton = this.currentRoute.includes('/movie/');
    });
  }

  goBack(): void {
    window.history.back();
  }

  goHome(): void {
    this.router.navigate(['/']);
  }
}
<div class="app">
  <header class="app-header">
    <div class="container">
      <div class="header-content">
        <div class="header-left">
          <button
            *ngIf="showBackButton"
            class="back-button"
            (click)="goBack()"
            aria-label="Вернуться назад"
          >
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" aria-hidden="true">
              <path d="M19 12H5M12 19l-7-7 7-7" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>

          <h1 class="app-title" (click)="goHome()" role="button" tabindex="0" aria-label="Главная страница">
            {{ title }}
          </h1>
        </div>

        <nav class="header-nav" aria-label="Основная навигация">
          <a
            routerLink="/"
            routerLinkActive="active"
            aria-label="Список фильмов"
          >
            Все фильмы
          </a>
          <a
            *ngIf="currentRoute.includes('/movie/')"
            class="current-movie"
            aria-current="page"
          >
            Детали фильма
          </a>
        </nav>
      </div>
    </div>
  </header>

  <main class="app-main">
    <div class="container">
      <router-outlet></router-outlet>
    </div>
  </main>

  <footer class="app-footer">
    <div class="container">
      <p>© 2024 Каталог фильмов. Все права защищены.</p>
      <p>Данные предоставлены для демонстрационных целей.</p>
    </div>
  </footer>
</div>
